pub fn print<W: Write>(
        &self,
        stream: &mut W, /*, range_format: &str, bin_format: &str*/
    ) -> io::Result<()> {
        unsafe {
            let n = (*self.unwrap_shared()).n as isize;

            for i in 0isize..n {
                writeln!(
                    stream,
                    "{} {} {}",
                    *(*self.unwrap_shared()).range.offset(i),
                    *(*self.unwrap_shared()).range.offset(i + 1),
                    *(*self.unwrap_shared()).range.offset(i),
                )?;
            }

            Ok(())
        }
    }
