fn decrement_strong_count<T>(ptr: NonNull<u8>) {
    unsafe {
        let inner = ptr.cast::<ArcInner<T>>().as_ptr();
        if (*inner).strong_count.fetch_sub(1, SeqCst) == 1 {
            drop_in_place(&mut (*inner).data);
            decrement_weak_count::<T>(ptr);
        }
    }
}
