impl<T> Drop for PoolValue<T> {
    fn drop(&mut self) {
        if !self.ptr.is_null() {
            let ptr = self.ptr;
            self.ptr = std::ptr::null_mut();
            unsafe {
                UnsafeCell::raw_get(self.slots)
                    .as_mut()
                    .unwrap_unchecked()
                    .push(Box::from_raw(ptr))
            }
        }
    }
}
