impl<T: ?Sized, A: Alloc> Drop for Box<T, A> {
    fn drop(&mut self) {
        unsafe {
            let layout = Layout::for_value(self.ptr.as_ref());
            ptr::drop_in_place(self.ptr.as_ptr());
            if layout.size() != 0 {
                self.a.dealloc(self.ptr.cast(), layout);
            }
        }
    }
}
