impl<T, const N: usize> Drop for Slice<T, N> {
    #[inline(always)]
    fn drop(&mut self) {
        // SAFETY:
        // The slice guarantees that `alive` is all initialised and that the other data is uninit
        unsafe {
            drop_in_place(slice_assume_init_mut(&mut self.array[self.alive.clone()]));
        }
    }
}
