impl<T> Drop for Inner<T> {
    #[inline(always)]
    fn drop(&mut self) {
        let state = State(*self.state.get_mut());
        // Drop the wakers if they are present
        if state.recv() {
            unsafe { drop_in_place((&mut *self.recv.get()).as_mut_ptr()); }
        }
        if state.send() {
            unsafe { drop_in_place((&mut *self.send.get()).as_mut_ptr()); }
        }
    }
}
