impl Drop for Table {
    fn drop(&mut self) {
        let len = self.entity_count();
        let cap = self.capacity();
        self.entities.clear();
        for col in self.columns.values_mut() {
            // SAFETY: `cap` and `len` are correct
            unsafe {
                col.drop(cap, len);
            }
        }
    }
}
