pub fn encode_vectored_max_len<T: Len, const N: usize>(
        &mut self,
        i: impl Iterator<Item = T>,
        mut encode: impl FnMut(T),
    ) -> bool {
        debug_assert!(N <= 64);
        let mut ptr = self.small.end_ptr();
        for t in i {
            let n = t.len();
            unsafe {
                *ptr = n as u8;
                ptr = ptr.add(1);
            }
            if n == 0 {
                continue;
            }
            if n > N {
                // Don't set end ptr (elements won't be saved).
                return true;
            }
            encode(t);
        }
        self.small.set_end_ptr(ptr);
        false
    }
