fn clone(&self) -> Self {
        let mut c = Self::new();
        c.set_alloc(self.alloc as usize);
        let mut n = self.len;
        if n > 0 {
            unsafe {
                let mut src = self.v.p.as_ptr();
                let mut dest = c.v.p.as_ptr();
                loop {
                    dest.write((*src).clone());
                    c.len += 1;
                    n -= 1;
                    if n == 0 {
                        break;
                    }
                    src = src.add(1);
                    dest = dest.add(1);
                }
            }
        }
        c
    }
