impl<T, A: BumpAllocator> Drop for DropGuard<T, A> {
            fn drop(&mut self) {
                unsafe {
                    let layout = Layout::from_size_align_unchecked(self.cap * T::SIZE, T::ALIGN);
                    self.allocator.deallocate(self.ptr.cast(), layout);
                }
            }
        }
