impl<T> Drop for BumpBoxSliceInitializer<'_, T> {
    fn drop(&mut self) {
        unsafe {
            let to_drop_len = self.init_len();
            let to_drop = non_null::slice_from_raw_parts(self.start, to_drop_len);
            to_drop.as_ptr().drop_in_place();
        }
    }
}
