impl Drop for AlignedBox {
  fn drop(&mut self) {
    let len = self.data.len();
    if len == 0 {
      return;
    }

    let ptr =
      ManuallyDrop::new(mem::replace(&mut self.data, [].into())).as_mut_ptr();

    unsafe {
      alloc::dealloc(
        ptr,
        alloc::Layout::from_size_align_unchecked(len, self.align),
      )
    }
  }
}
