pub fn or_insert_with<F: FnOnce() -> V>(self, fun: F) -> &'a mut V {
        match self.pl {
            EntryPayload::Occupied(res) => res,
            EntryPayload::Vacant {
                hash,
                key,
                value,
                count,
            } => {
                *hash = self.hash;
                unsafe {
                    std::ptr::write(key, self.key);
                    std::ptr::write(value, fun());
                    *count += 1;
                    &mut *value
                }
            }
        }
    }
