pub fn replace_if<F: Fn(T) -> bool>(&mut self, hash: u64, entry: T, replace: F) {
        let e = unsafe { self.table.get_unchecked_mut((hash as usize) & self.mask) };
        if replace(e.entry) {
            *e = CacheTableEntry {
                hash: hash,
                entry: entry,
            };
        }
    }
