pub fn map<S: 'static, R: RcLike<S>, F: FnOnce(&S) -> &T>(
    source: R,
    map_fn: F,
  ) -> RcRef<T> {
    let RcRef::<S> { rc, value } = source.into();
    // TODO(piscisaureus): safety comment
    #[allow(clippy::undocumented_unsafe_blocks)]
    let value = map_fn(unsafe { &*value });
    RcRef { rc, value }
  }
