impl<V: DeserializeOwned> Drop for Query<V> {
    fn drop(&mut self) {
        self.query_engine
            .active_requests
            .slab
            .borrow_mut()
            .remove(self.id);
        let queue_id = self.id;

        _ = self.query_engine.query_tx.send(format!(
            r#"
            if (!window.{QUEUE_NAME}) {{
                window.{QUEUE_NAME} = [];
            }}

            if (window.{QUEUE_NAME}[{queue_id}]) {{
                window.{QUEUE_NAME}[{queue_id}] = [];
            }}
            "#
        ));
    }
}
