pub fn new_fn<F: FnMut($direction_type) -> T>(mut f: F) -> Self {
                let values = unsafe {
                    let mut values: [T; $count] = MaybeUninit::uninit().assume_init();
                    for i in 0..$count {
                        values[i] = f(mem::transmute(i as u8));
                    }
                    values
                };
                Self { values }
            }
