impl<I, O> Drop for DeferStore<I, O> {
    #[inline]
    fn drop(&mut self) {
        // SAFETY: The correct variant is used based on `ONLY_INPUTS`.
        unsafe {
            if Self::ONLY_INPUTS {
                ManuallyDrop::drop(&mut self.inputs)
            } else {
                ManuallyDrop::drop(&mut self.slots)
            }
        }
    }
}
