pub fn set_user_data<K, T>(&mut self, key: K, data: T) -> Option<Box<dyn Any + 'static>>
    where
        K: ToString,
        T: Any + 'static,
    {
        unsafe {
            let any_map = get_any_map(self.ctx);
            (*any_map).insert(key.to_string(), Box::new(data))
        }
    }
