impl<A: Allocator> Drop for MemBuffer<A> {
    #[inline]
    fn drop(&mut self) {
        // SAFETY: this was initialized with std::alloc::alloc
        unsafe {
            self.alloc.deallocate(
                self.ptr,
                Layout::from_size_align_unchecked(self.len, self.align),
            )
        }
    }
}
