fn convert<F>(&mut self, f: F)
    where
        F: FnOnce(W) -> Self,
    {
        unsafe {
            let mut placeholder = MaybeUninit::uninit();
            mem::swap(self, &mut *placeholder.as_mut_ptr());
            let converted = f(placeholder.assume_init().into_inner());
            mem::forget(mem::replace(self, converted));
        }
    }
