impl<C: Ctxt> Drop for Frame<C> {
    fn drop(&mut self) {
        // SAFETY: We're being dropped, so won't access fields again
        let ctxt = unsafe { mem::ManuallyDrop::take(&mut self.ctxt) };
        let scope = unsafe { mem::ManuallyDrop::take(&mut self.scope) };

        ctxt.close(scope)
    }
}
