fn next(&mut self) -> Option<Self::Item> {
        let u = self.queue.pop_front()?;
        let visited_ptr = self.visited.as_mut_ptr();

        for v in self.digraph.out_neighbors(u) {
            let visited_v = unsafe { visited_ptr.add(v) };

            unsafe {
                if !*visited_v {
                    *visited_v = true;

                    self.queue.push_back(v);
                }
            }
        }

        Some(u)
    }
