pub fn get_users<T: From<libc::utmpx>>() -> Vec<T> {
    // TODO: Should we try to guess the capacity?
    let mut users = Vec::with_capacity(1);
    unsafe {
        libc::setutxent();
        loop {
            let entry = libc::getutxent();
            if entry.is_null() {
                break;
            }

            if (*entry).ut_type != libc::USER_PROCESS {
                continue;
            }

            users.push(T::from(*entry))
        }
        libc::endutxent();
    }

    users
}
