impl Drop for OnceAtom {
    fn drop(&mut self) {
        let ptr = *self.inner.get_mut();
        if !ptr.is_null() {
            // SAFETY: If the pointer is not null, we created it from
            // `Box::into_raw` in `Self::atom_get_or_init`.
            drop(unsafe { Box::from_raw(ptr) });
        }
    }
}
