impl Drop for DynamicManaged {
    fn drop(&mut self) {
        if self.drop {
            unsafe {
                if self.memory.is_null() {
                    return;
                }
                let data_pointer = self.memory.cast::<()>();
                (self.finalizer)(data_pointer);
                non_zero_dealloc(self.memory, self.layout);
                self.memory = std::ptr::null_mut();
            }
        }
    }
}
