pub unsafe fn map<T, U>(self, f: impl FnOnce(&T) -> &U) -> Option<Self> {
        if self.type_hash == TypeHash::of::<T>() {
            unsafe {
                let data = f(&*self.data.cast::<T>());
                Some(Self {
                    type_hash: TypeHash::of::<U>(),
                    lifetime: self.lifetime,
                    data: data as *const U as *const u8,
                })
            }
        } else {
            None
        }
    }
