pub fn set_static_field<'other_local, T, U>(
        &mut self,
        class: T,
        field: U,
        value: JValue,
    ) -> Result<()>
    where
        T: Desc<'local, JClass<'other_local>>,
        U: Desc<'local, JStaticFieldID>,
    {
        let class = class.lookup(self)?;
        let field = field.lookup(self)?;

        match value {
            JValue::Object(v) => jni_unchecked!(
                self.internal,
                SetStaticObjectField,
                class.as_ref().as_raw(),
                field.as_ref().into_raw(),
                v.as_raw()
            ),
            JValue::Byte(v) => jni_unchecked!(
                self.internal,
                SetStaticByteField,
                class.as_ref().as_raw(),
                field.as_ref().into_raw(),
                v
            ),
            JValue::Char(v) => jni_unchecked!(
                self.internal,
                SetStaticCharField,
                class.as_ref().as_raw(),
                field.as_ref().into_raw(),
                v
            ),
            JValue::Short(v) => jni_unchecked!(
                self.internal,
                SetStaticShortField,
                class.as_ref().as_raw(),
                field.as_ref().into_raw(),
                v
            ),
            JValue::Int(v) => jni_unchecked!(
                self.internal,
                SetStaticIntField,
                class.as_ref().as_raw(),
                field.as_ref().into_raw(),
                v
            ),
            JValue::Long(v) => jni_unchecked!(
                self.internal,
                SetStaticLongField,
                class.as_ref().as_raw(),
                field.as_ref().into_raw(),
                v
            ),
            JValue::Bool(v) => {
                jni_unchecked!(
                    self.internal,
                    SetStaticBooleanField,
                    class.as_ref().as_raw(),
                    field.as_ref().into_raw(),
                    v
                )
            }
            JValue::Float(v) => jni_unchecked!(
                self.internal,
                SetStaticFloatField,
                class.as_ref().as_raw(),
                field.as_ref().into_raw(),
                v
            ),
            JValue::Double(v) => {
                jni_unchecked!(
                    self.internal,
                    SetStaticDoubleField,
                    class.as_ref().as_raw(),
                    field.as_ref().into_raw(),
                    v
                )
            }
            JValue::Void => return Err(Error::WrongJValueType("void", "?")),
        }

        // Ensure that `class` isn't dropped before the JNI call returns.
        drop(class);

        Ok(())
    }
