        fn drop(&mut self) {
            let tag = self.tag();
            if tag.is_owned() {
                unsafe {
                    // SAFETY: `tag` ensures we are using the right variant
                    std::mem::ManuallyDrop::drop(&mut self.owned)
                }
            }
        }
    }
