extern "C" fn tell_func<R>(datasource: *mut libc::c_void) -> libc::c_long
        where
            R: Read + Seek,
        {
            let data: &mut DecoderData<R> = unsafe { &mut *(datasource as *mut DecoderData<R>) };
            data.reader
                .seek(io::SeekFrom::Current(0))
                .map(|v| v as libc::c_long)
                .unwrap_or(-1)
        }
