pub unsafe extern "C" fn frame_count<T: Wal>(
    wal: *mut wal_impl,
    locked: i32,
    out: *mut c_uint,
) -> c_int {
    let this = &mut (*(wal as *mut T));
    match this.frame_count(locked) {
        Ok(n) => {
            if !out.is_null() {
                unsafe {
                    *out = n as _;
                }
            }
            SQLITE_OK
        }
        Err(code) => code.extended_code,
    }
}
