impl<T> Drop for Inner<T> {
    fn drop(&mut self) {
        let state = State(*self.state.borrow());

        if state.is_rx_task_set() {
            unsafe {
                self.rx_task.drop_task();
            }
        }

        if state.is_tx_task_set() {
            unsafe {
                self.tx_task.drop_task();
            }
        }
    }
}
