impl<T> Drop for LinearPage<T> {
    #[inline]
    fn drop(&mut self) {
        let current = self.next.load(Ordering::Relaxed);
        if !current.is_null() {
            unsafe {
                // SAFETY: current was allocated with Box::new
                drop(Box::from_raw(current))
            };
        }
    }
}
