impl<'a, T> Drop for LinearReusable<'a, T> {
    #[inline]
    fn drop(&mut self) {
        let page = self.page;
        (self.pool.get_reset_callback())(unsafe {
            // SAFETY: there exists only this `LinearReusable` with this page_id
            page.get_mut(&self.page_id)
        });
        page.free(&self.page_id);
    }
}
