impl<T> Drop for MSliceIntoIter<T> {
    fn drop(&mut self) {
        unsafe {
            let base = self.ptr.as_ptr().add(self.begin);
            let len = self.end - self.begin;
            let slice = slice_from_raw_parts_mut(base, len);
            drop_in_place(slice);
            gen_free(self.ptr);
        }
    }
}
