extern "C" fn run_is_done_closure<F>(closure: *mut std::ffi::c_void) -> bool
  where
    F: (FnMut() -> bool),
  {
    let ptr = closure as *mut F;
    if ptr.is_null() {
      eprintln!("run_is_done_closure was called with a NULL pointer");
      return false;
    }
    let callback = unsafe { &mut *ptr };

    callback()
  }
