pub fn new<F: FnOnce(&'a mut T) -> R>(mut owned: Box<T>, f: F) -> OwningRefMut<T, R> {
        unsafe {
            let raw_owned = owned.deref_mut() as *mut _;
            let borrow = f(&mut *raw_owned);
            OwningRefMut {
                _owned: owned,
                borrow: Some(borrow)
            }
        }
    }
