unsafe impl<T: Send> Sync for SyncSender<T> {}
impl<T> Clone for SyncSender<T> {
    fn clone(&self) -> Self {
        SyncSender {
            inner: self.inner.clone(),
            queue_len: Arc::clone(&self.queue_len),
            queue_capacity: self.queue_capacity,
        }
    }
}
