impl<A: Allocator> Drop for BytesRefMut<'_, A> {
  #[inline]
  fn drop(&mut self) {
    if self.detach {
      return;
    }

    // SAFETY: offset and offset + size are inbounds of the ARENA.
    unsafe {
      self
        .arena
        .dealloc(self.allocated.memory_offset, self.allocated.memory_size);
    }
  }
}
