impl<T> Drop for Rc<T> {
    #[inline]
    fn drop(&mut self) {
        let counter = &self.inner().counter;
        let value = counter.get();
        unsafe {
            assume(value != 0);
        }
        if value != 1 {
            let value = value.wrapping_sub(1);
            counter.set(value);
        } else {
            unsafe { self.drop_slow() };
        }
    }
}
