pub fn new<I: Integer>(i: I) -> Self {
        let mut bytes = [MaybeUninit::<u8>::uninit(); I128_MAX_LEN];

		// SAFETY: dtolnay
        let (len, offset) = i.write(unsafe {
            &mut *(&mut bytes as *mut [MaybeUninit<u8>; I128_MAX_LEN]
                as *mut <I as private::Sealed>::Itoa)
        });

		Self {
			len: len as u8,
			offset: offset as u8,
			bytes,
		}
    }
