impl<T: ?Sized> Drop for Sc<T> {
  fn drop(&mut self) {
    self.inner().dec();
    if self.inner().ref_cnt() == 0 {
      unsafe {
        let layout = std::alloc::Layout::for_value(self.0.as_ref());
        let ptr = self.0.as_ptr();
        std::ptr::drop_in_place(ptr);
        std::alloc::dealloc(ptr as *mut _, layout)
      }
    }
  }
}
