impl Drop for SplitOnDrop<'_, '_> {
            fn drop(&mut self) {
                // Safety: the values of the complex number parts are individually valid.
                unsafe {
                    *self.1.inner_mut() = *self.0.real().inner();
                    *self.2.inner_mut() = *self.0.imag().inner();
                }
            }
        }
