impl Drop for Guard {
    fn drop(&mut self) {
        let old_env = self::no_std::rune_env_replace(self.env);

        unsafe {
            if let Some(context) = old_env.context {
                drop(Arc::from_raw(context.as_ptr().cast_const()));
            }

            if let Some(unit) = old_env.unit {
                drop(Arc::from_raw(unit.as_ptr().cast_const()));
            }
        }
    }
}
