fn filter_delta<T, U>(mut list: &mut DeltaList<T>, f: U)
where
    U: Fn(&T) -> bool,
{
    loop {
        match list {
            DeltaList::Nil => {
                return;
            }
            DeltaList::Cons(e) => {
                let front = e.get();
                let d_mut = unsafe { &mut (*front).1 };
                if f(d_mut) {
                    list = unsafe { &mut (*front).2 };
                } else {
                    let next = unsafe { &mut (*front).2 };
                    let next = std::mem::replace(next, DeltaList::Nil);
                    *list = next;
                }
            }
        }
    }
}
