impl Drop for Timer {
    #[inline]
    #[doc(alias = "SDL_RemoveTimer")]
    fn drop(&mut self) {
        unsafe {
            sys::timer::SDL_RemoveTimer(self.raw);
            if let Some(callback_ptr) = self.callback.take() {
                // Reclaim the Box and drop it.
                let _ = Box::from_raw(callback_ptr.as_ptr());
            }
        }
    }
}
