impl Drop for $name {
            #[inline]
            #[doc(alias = "SDL_QuitSubSystem")]
            fn drop(&mut self) {
                let prev_count = $counter.fetch_sub(1, Ordering::Relaxed);
                assert!(prev_count > 0);
                if prev_count == 1 {
                    unsafe {
                        sys::init::SDL_QuitSubSystem($flag);
                        // The last dropped subsystem instance "retrieves" an SdlDrop and drops it.
                        let _ = SdlDrop::new();
                    }
                }
            }
        }
