fn into_hex_raw<D>(&self, mut dst: D) -> ::std::result::Result<(), Self::Error>
            where
                D: ::std::io::Write,
            {
                let bytes: [u8; $bytes] = unsafe { ::std::mem::transmute(self.to_be()) };
                into_hex_bytearray!(bytes, dst, $bytes)?;
                Ok(())
