impl<A, T> Drop for LazyAtomicCell<A, T>
where
    A: 'static + GlobalAlloc,
{
    fn drop(&mut self) {
        let p = self.ptr.swap(ptr::null_mut(), Ordering::SeqCst);
        if p.is_null() {
            return;
        }
        unsafe {
            ptr::drop_in_place(p);
            self.allocator.dealloc(p.cast(), Layout::new::<T>());
        }
    }
}
