impl<A, T> Drop for PthreadMutex<A, T>
where
    A: 'static + GlobalAlloc,
{
    fn drop(&mut self) {
        unsafe {
            let retcode = libc::pthread_mutex_destroy(self.inner);
            assert_eq!(retcode, 0);

            let layout = Layout::new::<libc::pthread_mutex_t>();
            self.allocator.dealloc(self.inner.cast(), layout);
        }
    }
}
